INSERT INTO `wp_posts` VALUES (13, 1, '2013-01-16 19:32:30', '2013-01-16 19:32:30', '<h2 class="thin">This is the part where we learn aaaall about the client and their needs. We\'ll gather pre-existing assets--company logos, photos, colors, etc.--and determine the logistics of how the finished product will be delivered. We\'ll also start the conversation with the design and content folks so we can begin to hone in on a vision for the piece.</h2>', 'Discovery', '', 'publish', 'open', 'closed', '', 'discovery', '', '', '2013-02-14 23:21:29', '2013-02-14 23:21:29', '', 0, 'http://localhost:8080/process/?post_type=studio&#038;p=13', 1, 'studio', '', 0) ; 
