INSERT INTO `wp_posts` VALUES (73, 1, '2013-01-17 01:50:46', '2013-01-17 01:50:46', 'Get all your materials together, set up the room, order the food. Oh, and pick a Pandora station if that\'s part of your plan.', 'Setup', '', 'publish', 'open', 'closed', '', 'setup', '', '', '2013-02-15 21:25:51', '2013-02-15 21:25:51', '', 41, 'http://172.16.0.20/?post_type=studio&#038;p=73', 2, 'studio', '', 0) ; 
INSERT INTO `wp_posts` VALUES (75, 1, '2013-01-17 01:51:03', '2013-01-17 01:51:03', 'Lock the doors and hang on to your flip charts, it\'s go time.', 'Session', '', 'publish', 'open', 'closed', '', 'workingsessio_2', '', '', '2013-02-15 21:29:34', '2013-02-15 21:29:34', '', 41, 'http://172.16.0.20/?post_type=studio&#038;p=75', 3, 'studio', '', 0) ; 
INSERT INTO `wp_posts` VALUES (77, 1, '2013-01-17 01:51:22', '2013-01-17 01:51:22', 'Whew! Take a look at your copious notes, take a final pass at the Project Brief, take a nap.', 'Debrief', '', 'publish', 'open', 'closed', '', 'debrief', '', '', '2013-02-15 21:30:56', '2013-02-15 21:30:56', '', 41, 'http://172.16.0.20/?post_type=studio&#038;p=77', 4, 'studio', '', 0) ; 
INSERT INTO `wp_posts` VALUES (79, 1, '2013-01-17 01:51:35', '2013-01-17 01:51:35', 'Define your deadlines and make a date to get this thing out the door.', 'Timeline', '', 'publish', 'open', 'closed', '', 'timeline', '', '', '2013-02-15 21:33:27', '2013-02-15 21:33:27', '', 41, 'http://172.16.0.20/?post_type=studio&#038;p=79', 5, 'studio', '', 2) ; 
INSERT INTO `wp_posts` VALUES (81, 1, '2013-01-17 01:51:50', '2013-01-17 01:51:50', 'In preparation for the creating the moodboards and story concepts, we\'ll review notes from earlier meetings and take a look at the client\'s existing stuff—and stuff from their competitors, too. If all goes according to plan, by the end of this phase, we\'ll be off and running in the right direction.', 'Prepare', '', 'publish', 'open', 'closed', '', 'prepare', '', '', '2013-02-15 21:35:32', '2013-02-15 21:35:32', '', 43, 'http://172.16.0.20/?post_type=studio&#038;p=81', 1, 'studio', '', 0) ; 
INSERT INTO `wp_posts` VALUES (83, 1, '2013-01-17 01:52:02', '2013-01-17 01:52:02', 'While the Content Developer is toiling away on the story concepts, the Design Lead can get crackin\' on the moodboards. Each of these deliverables will be accompanied by a good talk track that will help the client understand our rationale and envision how these concepts will affect the end product.', 'Produce', '', 'publish', 'open', 'closed', '', 'produce', '', '', '2013-02-15 22:01:38', '2013-02-15 22:01:38', '', 43, 'http://172.16.0.20/?post_type=studio&#038;p=83', 2, 'studio', '', 0) ; 
INSERT INTO `wp_posts` VALUES (85, 1, '2013-01-17 01:52:28', '2013-01-17 01:52:28', 'Get in a room with the internal team and take a look at the story concepts and/or moodboards to see how we can add some collaborative energy to the product.', 'Preview', '', 'publish', 'open', 'closed', '', 'preview', '', '', '2013-02-15 22:03:38', '2013-02-15 22:03:38', '', 43, 'http://172.16.0.20/?post_type=studio&#038;p=85', 3, 'studio', '', 0) ; 
INSERT INTO `wp_posts` VALUES (87, 1, '2013-01-17 01:52:57', '2013-01-17 01:52:57', 'Take the internal feedback into account and knock out that last-minute pixel-pushing and wordsmithing.', 'Polish', '', 'publish', 'open', 'closed', '', 'polish', '', '', '2013-02-15 22:07:13', '2013-02-15 22:07:13', '', 43, 'http://172.16.0.20/?post_type=studio&#038;p=87', 4, 'studio', '', 0) ; 
INSERT INTO `wp_posts` VALUES (89, 1, '2013-01-17 01:53:23', '2013-01-17 01:53:23', 'Walk the client through the Story Concepts and Moodboards. The goal here is to help them envision the overall vibe of the final presentation.', 'Pitch', '', 'publish', 'open', 'closed', '', 'pitch', '', '', '2013-02-15 22:12:14', '2013-02-15 22:12:14', '', 43, 'http://172.16.0.20/?post_type=studio&#038;p=89', 5, 'studio', '', 0) ; 
INSERT INTO `wp_posts` VALUES (91, 1, '2013-01-17 01:53:36', '2013-01-17 01:53:36', 'Take a step back, take a breath, collect your thoughts. Here we discuss what happened in the  Client Pitch and make sure to capture new information in the Project Brief.', 'Post', '', 'publish', 'open', 'closed', '', 'post', '', '', '2013-02-15 22:12:32', '2013-02-15 22:12:32', '', 43, 'http://172.16.0.20/?post_type=studio&#038;p=91', 6, 'studio', '', 1) ; 
