INSERT INTO `wp_posts` VALUES (95, 1, '2013-01-17 01:54:12', '2013-01-17 01:54:12', 'Let\'s have an internal look-see to talk about what we like, and how we can make it extra good before the client pitch.', 'Preview', '', 'publish', 'open', 'closed', '', 'preview', '', '', '2013-02-15 22:40:31', '2013-02-15 22:40:31', '', 45, 'http://172.16.0.20/?post_type=studio&#038;p=95', 2, 'studio', '', 0) ; 
