INSERT INTO `wp_posts` VALUES (618, 1, '2013-02-01 00:36:15', '2013-02-01 00:36:15', 'Time to put our heads together and devise a plan to make some visuals that will really resonate (like the book!) with the client, fit the delivery method, and connect with the audience. Extra points for standing ovations and effusive thank you e-mails.', 'Prepare', '', 'publish', 'open', 'open', '', 'prepare', '', '', '2013-02-15 22:15:43', '2013-02-15 22:15:43', '', 45, 'http://172.16.0.20/?post_type=studio&#038;p=618', 0, 'studio', '', 0) ; 
